function useLocalStorage(key, initialValue) {
    // Try to read from local storage
    const storedValue = localStorage.getItem(key);
    const initial = storedValue ? JSON.parse(storedValue) : initialValue;
  
    const [value, setValue] = useState(initial);
  
    useEffect(() => {
      // Update local storage when value changes
      localStorage.setItem(key, JSON.stringify(value));
    }, [key, value]);
  
    return [value, setValue];
  }
  
  function useAxios(baseUrl, dataFormatter) {
    // Use useLocalStorage instead of useState
    const [data, setData] = useLocalStorage("data", []);
  
    const fetchData = async (url) => {
      try {
        const response = await axios.get(url);
        const formattedData = dataFormatter(response.data); // Format the data
        setData((prevData) => [...prevData, formattedData]);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
  
    const addData = async (restOfUrl) => {
      const url = baseUrl + restOfUrl;
      await fetchData(url);
    };
  
    const clearData = () => {
      setData([]);
    };
  
    return [data, addData, clearData];
  }
  
  export { useFlip, useLocalStorage, useAxios };
